///Step

if ( global.menustate == "OPEN" ) {

    vx = 0;
    vy = 0;

} else if ( global.dialogue_is_open ) {

    vx = 0;
    vy = 0;
    image_speed = 0;

} else {

    switch( global.state ) {

        case "IDLE":
            image_speed = 0;
            vx = 0;
            vy = 0;
            sprite_index = sprite_walk;
            image_index = 0;
            check_inputs_all();
        break;

        case "UP":
            image_speed = anim_speed;
            global.facing = 0;
            sprite_walk = spr_player_up;
            //sprite_use = spr_player_use_up;
            sprite_index = sprite_walk;
            vx = 0;
            vy = -1 * global.player_speed;
            check_inputs_all();
        break;

        case "DOWN":
            image_speed = anim_speed;
            global.facing = 2;
            sprite_walk = spr_player_down;
            //sprite_use = spr_player_use_down;
            sprite_index = sprite_walk;
            vx = 0;
            vy = 1 * global.player_speed;
            check_inputs_all();
        break;

        case "LEFT":
            image_speed = anim_speed;
            global.facing = 3;
            sprite_walk = spr_player_left;
            //sprite_use = spr_player_use_left;
            sprite_index = sprite_walk;
            vx = -1 * global.player_speed;
            vy = 0;
            check_inputs_all();
        break;

        case "RIGHT":
            image_speed = anim_speed;
            global.facing = 1;
            sprite_walk = spr_player_right;
            //sprite_use = spr_player_use_right;
            sprite_index = sprite_walk;
            vx = 1 * global.player_speed;
            vy = 0;
            check_inputs_all();
        break;
        /*
        case "USE_START":
            image_speed = anim_speed;
            vx = 0;
            vy = 0;
            timer = 15;

            sprite_index = sprite_use;
            global.state =  "USE";
        break;
        case "USE":
        if (timer >0) {
            timer -= 1;
         } else {
            global.state = "IDLE";
            }
         break;
         */
         case "CUTSCENE":
            vx = 0;
            vy = 0;
            image_speed=0;
         break;

     }

}

//Perform movement + collision checking
var _x_sign = sign( vx ); //x_velocity is the same thing as "hspeed" but a custom variable
var _y_sign = sign( vy ); //y_velocity is the same thing as "vspeed" but a custom variable
var _x_abs  = abs(  vx );
var _y_abs  = abs(  vy );

repeat( _x_abs ) {
    if ( place_meeting( x + _x_sign, y, obj_solid_parent ) ) {
        break;
    } else {
        x += _x_sign;
    }
}
repeat( _y_abs ) {
    if ( place_meeting( x, y + _y_sign, obj_solid_parent ) ) {
        break;
    } else {
        y += _y_sign;
    }
}
