#include <iostream>

using namespace std;

int main()
{
    int x;
    cin >> x;
    if (x % 100 >= 10 && x % 100 <= 20) {
        cout << " " << x << " ";
        return 0;
    }

    if (x % 10 == 2 || x % 10 == 3 || x % 10 == 4) {
        cout << " " << x << " ";
        return 0;
    }

    if (x % 10 == 1) {
        cout << " " << x << " ";
        return 0;
    }

    cout << " " << x << " ";

    return 0;
}
