#include <stdlib.h>
#include <stdio.h>
#include <unistd.h>
#include <sys/mman.h>

int
main(void)
{
        if (mmap((void*)(0xffffffff - 4096), 1, PROT_READ|PROT_WRITE|PROT_EXEC, MAP_FIXED|MAP_ANONYMOUS|MAP_PRIVATE, -1, 0) == MAP_FAILED) {                                                                     
                perror("mmap");
                exit(EXIT_FAILURE);
        }   
        return EXIT_SUCCESS;
}
