$(document).ready(function(){
  $(".vhost_user_edit").editable(location.href + ".json", {
    type      : 'textarea',
    cancel    : 'Cancel',
    submit    : 'Save',
    name      : 'vhost_user',
    indicator : "<img src='/images/indicator.gif'>",
    target    : location.href + "?ajax=true",
    loadtype  : "GET",
    loadurl   : location.href + ".json?ajax=true",
    tooltip   : 'Click to edit...',
    method : "PUT"
  });
});
