{
  "Host": {
    "ProfitTrailerFolder1": "/ProfitTrailer", // add your in one or more PT folders below, where ths application.properties file is. Note that windows users must add \\ in between the folders, NOT \
    "HostName": "My PT Feeder bot1", // the name that is used on telegram messages
    "LicenseKey": "xxxx-xxxx-xxxx-xxxx-xxxx-xxxx", // License key for PT Feeder
    "ServerUrls": "http://localhost:5010",  
    "MarketConditionCheckInMinutes": "3", // How often to check for changes.
    "CalculateTrailingValues": "true", // whether PT Feeder should calculate the trading values for you
    "UseMaxCostPercentage": "true", // whether cost should be as a percentage
    "UseMinBuyBalancePercentage": "true", // whether minbuybalance should be a percentage
    "BackupTradingFiles": "true", // whether PT Feeder automatically backups PT properties files on startup
    "TelegramBotId": "", // telegram bot id
    "TelegramChatId": "", // telegram chat id
    "TestMode": "true" // whether watchmode should be enabled or not
  },

  "Serilog": {
    "MinimumLevel": "Information" // Can be Information or Debug depending how much logging you want
  }
}
