#include<stdio.h>
int main()
{
    int i,s, GivenArray[100];
    printf("Enter the size of array : ");
    scanf("%d",&s);
    printf("Enter elements of the array...\n");
    for(i=0;i<s;i++)
    {
        scanf("%d",&GivenArray[i]);
    }

    printf("Your entered array is ");
    for(i=0;i<s;i++)
    {
        printf("%d ",GivenArray[i]);
    }
    return 0;
}
