public void act(int whichStock, Action action, int amount) {
    Stock stock01 = stock1;
    Stock stock02 = stock2;
    Stock stock03 = stock3;
    //test if should be 2 or 3

    switch (action) {
        case UP:
            //handle a stock
            if (stock01.getValue() + amount > 200) {
                handleSplit(stock);

            } else {
                switch (whichStock) {
                    case 1:
                        stock01.setValue(stock01.getValue() + amount);
                        break;
                    case 2:
                        stock02.setValue(stock02.getValue() + amount);
                        break;
                    case 3:
                        stock03.setValue(stock03.getValue() + amount);
                        break;
                }
            }
            break;
        case DOWN:
            if (stock01.getValue() + amount < -200) {
                handleSplit(stock);

            } else {
                switch (whichStock) {
                    case 1:
                        stock01.setValue(stock01.getValue() - amount);
                        break;
                    case 2:
                        stock02.setValue(stock02.getValue() - amount);
                        break;
                    case 3:
                        stock03.setValue(stock03.getValue() - amount);
                        break;
                }
            }
            break;
        case DIV:
            switch (whichStock) {
                case 1:
                    if (stock01.getValue() <= 100) {
                        break;
                    }
                    player.setHolding1(player.getHolding1() + ((player.getHolding1() * amount) / 100));
                    break;
                case 2:
                    if (stock02.getValue() <= 100) {
                        break;
                    }
                    player.setHolding2(player.getHolding2() + ((player.getHolding2() * amount) / 100));
                    break;
                case 3:
                    if (stock03.getValue() <= 100) {
                        break;
                    }
                    player.setHolding3(player.getHolding3() + ((player.getHolding3() * amount) / 100));
                    break;
            }
            break;
    }
}
