#include <iostream>
using namespace std;

// LCM

/*
*/


int gcd(int a, int b) {
	if (b == 0) {
		return a;
	}
	else {
		return gcd(b, a%b);
	}
}


int main(void) {

	//freopen("input.txt", "r", stdin);

	int T;
	cin >> T;

	while(T--) {
		int a, b;
		cin >> a >> b;
		long g;
		g = gcd(a, b);

		long lcm;
		lcm = (a*b) / g;
		cout << lcm << endl;
	}	
}
