#include <iostream>

using namespace std;

int numeros[10];
int min;


void ordenar(){
	int aux;
	for(int j=0; j<9; j++){
		for(int k=j+1; k<10; k++){
			if(numeros[k]<numeros[j]){
				aux = numeros[k];
				numeros[k]=numeros[j];
				numeros[j]=aux;
			}
		}
	}
}

int main(){

	for (int i = 0; i < 10; i++)
	{
		cout << "Introduce un nmero "<< i <<": "<< endl;
		cin >>numeros[i];
	}
	//mostrar arreglo sin ordenar
	cout << "arreglo sin ordenar" << endl;
	for (int j = 0; j < 10; j++)
	{
		cout << numeros[j] << endl;
	}

	//ordenar por seleccion
	ordenar();

	//mostrar arreglo ordenado
	cout << "arreglo ordenado" << endl;
	for (int m = 0; m < 10; m++)
	{
		cout << numeros[m] << endl;
	}
	return 0;
}
