text:
/* Css */
* {
	margin:0;
	padding:0;
	-webkit-font-smoothing: antialiased;
}
body {
    font-family: "Helvetica" !important;
}
#main-wrapper {
    position: relative;
    overflow: hidden;
    width: 380px;
    height: 440px;
    margin: 50px calc(50% - 190px);
    box-shadow: 0 0 30px rgba(0,0,0,.2);
    margin-bottom: 0;
    margin-top: 50px;
}
#chat-wrapper {
    position: relative;
    padding-bottom: 10px;
    width: 100%;
    height: 440px;
    background-size: 130%;
    position: relative;
    background-image: url(../img/sfondo-wa.png);
}
#chat-header {
    z-index: 1;
    color: #3478f7;
    width: 100%;
    position: absolute;
    padding: 10px 10px;
    background-color: #f7f7f7;
    border-bottom: 1px solid #dadada;
    box-shadow: 0 1px 5px rgba(0,0,0,.05);
}
.icon {
    float: left;
    line-height: 40px;
    font-size: 20px;
    font-weight: 200;
    margin-right: 8px;
}
.number {
    line-height: 40px;
    font-weight: 500;
    font-size: 18px;
    position: relative;
    margin-right: 10px;
    float: left;
}
.info {
    float: left;
}
.group-name {
    width: 100%;
    line-height: 22px;
    font-size: 17px;
    float: left;
    font-weight: 800;
    color: #040404;
}
.group-picture {
    height: 40px;
    width: 40px;
    background-repeat: no-repeat;
    background-size: cover;
    margin-right: 10px;
    border-radius: 30px;
    float: left;
}
.group-label {
    width: 100%;
    line-height: 18px;
    color: #8c8c8c;
    float: left;
    font-size: 14px;
    font-weight: 400;
}
#chat-messages-wrapper {
    padding: 0 15px;
    position: absolute;
    top: 59px;
    bottom: 0;
    right: 0;
    left: 0;
    overflow-y: scroll;
}
::-webkit-scrollbar { 
    display: none; 
}
#message-wrapper {
    width: fit-content;
    display: block;
    font-size: 16px;
    padding: 7.5px;
    padding-bottom: 5px;
    background-color: #fff;
    border-radius: 5px;
    margin-bottom: 2.5px;
    box-shadow: 0 2px 5px rgba(0,0,0,.1);
    float: left;
    clear: left;
}
#message-wrapper img {
    max-width: 100%;
    margin-bottom: 5px;
}
.message-date {
    text-align: right;
    font-size: 12px;
    color: #b9b9b9;
}
.message-date svg {

}
.message-date-right {
    position: relative;
    padding-right: 15px;
}
.message-date-right:after {
    position: absolute;
    content: "";
    top: 4.25px;
    right: 0;
    width: 13px;
    height: 9px;
    background-size: contain;
    background-image: url(../img/seen.png);
    background-repeat: no-repeat;
}
.message-received-name {
    display: none;
    font-size: 16px;
    margin-bottom: 2.5px;
    font-weight: 800;
}
.message-single-user-wrapper {
    width: 100%;
    margin-top: 10px;
}
.message-single-user-wrapper:last-child {
    margin-bottom: 10px;
}
#message-wrapper:first-child > .message-received-name {
    display: block;
}
#message-wrapper:last-child {
    position: relative;
}
#message-wrapper:after {
    content: "";
    position: absolute;
    left: -7px;
    bottom: 5px;
    width: 8px;
    height: 9px;
    background-size: cover;
    background-image: url(../img/balloon.png);
}
.message-wrapper-personal {
    float: right !important;
    clear: right !important;
    background-color: #e1f6cb !important;
}

.message-wrapper-personal:after {
    width: 0px !important;
    height: 0px !important;
}
.message-wrapper-personal:before {
    content: "";
    position: absolute;
    right: -7px;
    bottom: 5px;
    width: 7px;
    height: 9px;
    background-size: cover;
    background-image: url(../img/balloon-2.png);
}
.add-or-abandon {
    margin: 10px 0;
    font-size: 14px;
    text-align: center;
    float: left;
    width: 100%;
    display: flex;
    justify-content: center;
}
.add-or-abandon div {
    width: fit-content;
    padding: 5px;
    border-radius: 5px;
    background-color: #dff1f5;
    box-shadow: 0 2px 5px rgba(0,0,0,.1);
}
.whatsapp-notification-wrapper {
    position: absolute;
    z-index: 999999;
    width: 100%;
    float: left;
    padding: 15px;
    background-color: rgba(0,0,0,.9);
    color: #fff;
    font-size: 14px;
}
.notification-profile-pic {
    width: 40px;
    height: 40px;
    float: left;
    border-radius: 20px;
    margin-right: 10px;
    background-size: cover;
}
.notification-sender {
    font-size: 16px;
    line-height: 18px;
    font-weight: 800;
}
.vocal-message-wrapper {
    padding: 5px 5px 5px 10px;
}
.vocal-message-wrapper .fa {
    color: #969696;
    font-size: 18px;
    float: left;
}
.vocal-message-bar {
    margin-top: 8px;
    width: 140px;
    height: 2.25px;
    margin-left: 30px;
    background-color: rgb(226, 226, 226);
    position: relative;
}
.vocal-message-bar:after {
    content: "";
    width: 10px;
    height: 10px;
    position: absolute;
    top: -3.875px;
    left: -1px;
    border-radius: 10px;
    background-color: #09d261;
}
.message-vocale-duration {
    text-align: left;
    float: left;
    font-size: 9px;
    color: #b9b9b9;
}
.vocal-text {
    float: left;
    margin-left: 30px;
    width: calc(100% - 30px);
}
.vocalProfileImage {
    float: left;
    position: relative;
    width: 40px;
    height: 40px;
    top: -7px;
    margin-left: 8px;
    border-radius: 20px;
    margin-right: 2.5px;
    background-size: cover;
    background-repeat: no-repeat;
}
#keyboard-wrapper {
    width: 380px;
    float: left;
    margin-left: calc(50% - 190px);
}
.chat-baloon-wrapper {
    width: 100%;
    height: 50px;
    margin-bottom: -6px;
}
.chat-baloon-wrapper img {
    width: 100%;
}
textarea {
    resize: none;
    position: relative;
    font-size: 12pt;
    width: 295px;
    max-height: 21px;
    top: -31.5px;
    left: 60px;
    border: 0;
}
.message {
    max-width: 270px;
}
#background-wrapper {
    position: fixed;
    left: 50%;
    margin-left: -275px;
    top: 50px;
    width: 550px;
    height: 700px;
    z-index: -9;
    background-size: cover;
}
