public class EsFiltro{
	public static void car(int n){
		char val='A';
		for(int i=0;i<n;i++){
			System.out.print(val);
			if(i!=n-1)
				System.out.print(" ");
			val=(char)(val+1);
			}
	}

	public static void main(String[] args){
		int n=Integer.parseInt(args[0]);
		if(n<0)
			System.out.println("Errore");
		else
			if(n>0) {
				if(n>1)
					for(int i=1; i<n; i++) {
						car(i);
						System.out.println();
					}
				car(n);
				if(n>1){
					System.out.println();
					for(int j=n-1;j>=1;j--) {
						car(j);
						if(j!=1)
						System.out.println();
					}
				}
			}
	}
}
