import java.io.*;
import java.util.*;
import java.text.*;
import java.math.*;
import java.util.regex.*;

public class Solution {
    static void addElement(int a,TreeMap t)
    {   
        if(t.get(a)==null)
            t.put(a,1);
        else if(t.get(a)!=null && t.get(a+1)==null)
            t.put(a+1,1);
        else if(t.get(a)!=null && t.get(a+1)!=null && t.get(a-1)==null)
            t.put(a-1,1);
        else if(t.get(a)!=null && t.get(a+1)!=null && t.get(a-1)!=null)
        {
            if((t.get(a)==t.get(a+1) && (int)t.get(a)<(int)t.get(a-1))||(t.get(a)==t.get(a-1) && (int)t.get(a)<(int)t.get(a+1))||(t.get(a)==t.get(a+1) && t.get(a)==t.get(a-1)))
                t.put(a,(int)t.get(a)+1);
            else if((int)t.get(a)>(int)t.get(a+1))
                addElement(a+1,t);
            else if((int)t.get(a)>(int)t.get(a-1))
                addElement(a-1,t);
        }

    }
    static void printElement(int[] k,int[] v,int n)
    {
        int z=0;
        for(int i=0;i<n;i++)
        {
            if(v[i]==0)
                z=1;
            else if(v[i]>0)
            {
                z=0;
                break;
            }
        }
        if(z==1)
            return;
        int[] t=new int[n];
        for(int i=0;i<n;i++)
        {
            t[i]=v[i];
            if(v[i]>0)
                v[i]--;
        }
        printElement(k,v,n);
        System.out.println("");
        for(int i=0;i<n;i++)
        {
            if(t[i]==0)
                System.out.print(".");
            else
            {
                System.out.print("O");
                t[i]--;
                v[i]--;
            }
            if(i<n-1 && k[i]+1!=k[i+1])
            {
                for(int j=k[i]+1;j<k[i+1];j++)
                    System.out.print(".");
            }
        }
    }

    public static void main(String[] args) throws IOException{
        /* Enter your code here. Read input from STDIN. Print output to STDOUT. Your class should be named Solution. */
        BufferedReader br=new BufferedReader(new InputStreamReader(System.in));
        int T,n;
        T=Integer.parseInt(br.readLine());
        for(int i=1;i<=T;i++)
        {
            System.out.print("Case "+i+":");
            n=Integer.parseInt(br.readLine());
            int[] a=new int[n];
            String line=br.readLine();
            String[] str=line.trim().split("\\s+");
            for(int j=0;j<n;j++)
                a[j]=Integer.parseInt(str[j]);  
            TreeMap<Integer,Integer> t=new TreeMap<Integer,Integer>();
            for(int j=0;j<n;j++)
            {
                addElement(a[j],t);
                /*if(t.get(a[j])==null)
                    t.add(a[j],1);
                else if(t.get(a[j])!=null && t.get(a[j]+1)==null)
                    t.add(a[j]+1,1);
                else if(t.get(a[j])!=null && t.get(a[j]+1)!=null && t.get(a[j]-1)==null)
                    t.add(a[j]-1,1);*/
            }
            int n1=t.size();
            int[] k=new int[n1];
            int[] v=new int[n1];
            int z=0;
            for(Map.Entry<Integer,Integer> e:t.entrySet())
            {
                k[z]=e.getKey().intValue();
                v[z]=e.getValue().intValue();
                z++;
            }
            /*for(int j=0;j<n1;j++)
                System.out.print(k[j]+" ");
            System.out.println("");
            for(int j=0;j<n1;j++)
                System.out.print(v[j]+" ");*/
            printElement(k,v,n1);
            System.out.println("");
        }
    }
}
