text:
options:
    kabu: &7[&e&r&7]&r

variables:
    {kabu.money} = 1000

command /kabu [<text>] [<number>]:
    permission: skript.kabu
    description: &e
    trigger:
        if arg 1 is not set:
            send "{@kabu} 1: buy/sell/check"
        else if arg 1 is "buy":
            if arg 2 is not set:
                send "{@kabu} "
            else if arg 2 is set:
                send "{@kabu}  %arg 2% "
                loop arg 2 times:
                    execute console command "eco take %player% %{kabu.money}%"
                    add arg 2 to {kabu.have.%player%}
                    add 1 to {kabu.mcheck}
        else if arg 1 is "sell":
            if arg 2 is not set:
                send "{@kabu} "
            else if arg 2 is set:
                if {kabu.have.%player%} is greater than or equal to arg-2:
                    send "{@kabu}  %arg 2% "
                    loop arg 2 times:
                        execute console command "eco give %player% %{kabu.money}%"
                        remove arg 2 from {kabu.have.%player%}
                        remove 1 from {kabu.mcheck}
                else:
                    send "{@kabu}  %arg 2% "
                    send "{@kabu}  %{kabu.have.%player%}% "
        else if arg 1 is "check":
            send "{@kabu} : %{kabu.have.%player%}%"
            send "{@kabu} : %{kabu.money}%"

every 5 minutes:
    broadcast "{@kabu} &a %{kabu.plus}%   %{kabu.money}% "
    set {kabu.trigger} to a random number between 1 to 4
    if {kabu.trigger} is between 1 and 2:
        add {kabu.plus} to {kabu.money}
    else if {kabu.trigger} is between 3 and 4:
        remove {kabu.plus} from {kabu.money}

every 1 minutes:
    set {kabu.plus} to 0
    set {kabu.addplus} to a random number between 1 to 40
    if {kabu.addplus} is between 1 and 2:
        add 100 to {kabu.plus}
    else if {kabu.addplus} is between 3 and 4:
        add 200 to {kabu.plus}
    else if {kabu.addplus} is between 5 and 6:
        add 400 to {kabu.plus}
    else if {kabu.addplus} is between 7 and 8:
        add 800 to {kabu.plus}
    else if {kabu.addplus} is between 9 and 10:
        add 1600 to {kabu.plus}
    else if {kabu.addplus} is between 11 and 12:
        add 3200 to {kabu.plus}
    else if {kabu.addplus} is between 13 and 14:
        add 6400 to {kabu.plus}
    else if {kabu.addplus} is between 15 and 16:
        add 12800 to {kabu.plus}
    else if {kabu.addplus} is between 17 and 18:
        add 25600 to {kabu.plus}
    else if {kabu.addplus} is between 19 and 20:
        add 51200 to {kabu.plus}
    else if {kabu.addplus} is between 21 and 22:
        remove 100 from {kabu.plus}
    else if {kabu.addplus} is between 23 and 24:
        remove 200 from {kabu.plus}
    else if {kabu.addplus} is between 25 and 26:
        remove 400 from {kabu.plus}
    else if {kabu.addplus} is between 27 and 28:
        remove 800 from {kabu.plus}
    else if {kabu.addplus} is between 29 and 30:
        remove 1600 from {kabu.plus}
    else if {kabu.addplus} is between 31 and 32:
        remove 3200 from {kabu.plus}
    else if {kabu.addplus} is between 33 and 34:
        remove 6400 from {kabu.plus}
    else if {kabu.addplus} is between 35 and 36:
        remove 12800 from {kabu.plus}
    else if {kabu.addplus} is between 37 and 38:
        remove 25600 from {kabu.plus}
    else if {kabu.addplus} is between 39 and 40:
        remove 51200 from {kabu.plus}

command /kabuset [<number>]:
    permission: skript.kabu
    trigger:
        if arg 1 is not set:
            send "{@kabu} OP"
        else if arg 1 is set:
            send "{@kabu} "
            set {kabu.money} to arg 1
            broadcast "{@kabu} %player%  &d%{kabu.money}%"
            execute console command "eco take %player% 1000000000"
            execute console command "broadcast &d%player%  "
