void inserare(nod *&p)
{
    nod*e,*f,*g,*urmator;
    e=p;
    while(e!=NULL)
    {
        g=e->urm;
        if(e->info%2==0)
        {
            f=new nod;
            f->info=2*e->info;
            prec=p;
            while(prec->urm!=e)
                prec=prec->urm;
            f->urm=e;
            prec->urm=f;
        }
        e=g;
    }

}
