public void setTempo(int hora, int minuto, int segundo) {
      this.segundo = segundo;
      int m = 0;
      if (this.segundo >= 60) {
          do {
              this.segundo = this.segundo - 60;
              m++;
          } while (this.segundo >= 60);
          this.minuto = minuto + m;
      }
      if (this.minuto >= 60) {
          int h = 0;
          do {
              this.minuto = this.minuto - 60;
              h++;
          } while (this.minuto >= 60);
          if (m >= 60) {
              this.hora = hora + h;
          }
      }
  }
