#include <iostream>
#include<string.h>
#include<stdio.h>
using namespace std;
char s[100],aux[100];
int main()
{int i,j;
j=0;
gets(s);
for(i=0;i<strlen(s);i++)
if(s[i]==' '&&s[i-1]!=' ')
  if((i-j)%2==0)
  {
      strcpy(aux,s+i);
      strcpy(s+j,'#');
      strcpy(s+j+1,aux);
  }
  j=j+2;
  i=j;
  else
    j=i+1;
}
