            Console.WriteLine("Er det rigtigt du er " + alder + " r?");
            Console.WriteLine("Er dette valgt rigtigt? Y/N");
            string answer1 = Console.ReadLine();
            do
            {
                Console.WriteLine("Okay, vi prver igen... Hvor gammel er du?");
                alder = Console.ReadLine();
                Console.WriteLine("Er det rigtigt du er " + alder + " r?");
                Console.WriteLine("Er dette valg rigtigt? Y/N");
                answer1 = Console.ReadLine();
            }
            while (answer1 == "N");
            Console.WriteLine(alder);
            Console.ReadKey();
        }
    }
}
