#include <iostream>
#include <string>
#include <iomanip>
#include <cmath>

using namespace std;

//Exercise 5.11 Find the smallest value
void findSmallestValue()
{
    int inputAmount{0};
    int currentNumber{0};
    int smallestNumber;

    cout << "How many values are you going to input" << endl;
    cin >> inputAmount;

    for(int i{0}; i < inputAmount; i += 1)
    {
        cout << "Enter Number: ";
        cin >> currentNumber;
        cout << "\n";

        if(currentNumber < smallestNumber)
        {
            smallestNumber = currentNumber;
        }
    }

    cout << "The smallest number is: " << smallestNumber << endl;
}

//Exercise 5.12 Calculate the products of odd integers from 1 to 15
void findOddProducts ()
{
    int total{1};

    for(int i{1}; i <= 15; i += 2)
    {
        total *= i;
    }

    cout << total << endl;
}

//Exercise 5.13 print factorial from 1-20 in tabular format
void printFirstRow()
{
    for(int i{1}; i <= 20; i += 1)
    {
        if(i < 10)
        {
            //Print 3 spaces
            cout << i << "   ";
        }
        else
        {
            //print 2 spaces
            cout << i << "  ";
        }
    }
    cout << endl;
}

int getFactorial(int num)
{
    int factorial{0};

    if(num > 1)
    {
        for(int i {1}; i <= num; i += 1)
        {
            factorial += i;
        }
    }
    else
    {
        factorial = 1;
    }

    return factorial;
}

//Main function
void factorialToTwenty()
{
    printFirstRow();

    string factorial;

    for(int i{1}; i <= 20; i += 1)
    {
        factorial = to_string(getFactorial(i));

        //Determine how many character it is so we know how many spaces to add in between
        if(factorial.length() == 1)
        {
            //Print 3 spaces
            cout << factorial << "   ";
        }
        else if(factorial.length() == 2)
        {
            //Print 2 spaces
            cout << factorial << "  ";
        }
        else if(factorial.length() == 3)
        {
            //Print 1 space
            cout << factorial << " ";
        }


    }
}

//Exercise 5.14 modify the interest calculator to include rates .05 - .10
void calcInterest()
{
    double newRate{0.05};

    for(int i{1}; i <= 5; i += 1)
    {
        //Set floating point number format
        cout << fixed << setprecision(2);

        double principal{1000.00}; //initial amount before interest
        double rate{newRate}; //interest rate

        cout << "Initial Principle: " << principal << endl;
        cout << "     Initial rate:    " << rate << endl;

        //display headers
        cout << "\nYear" << setw(20) << "Amount on deposit" << endl;

        //calculate amount on deposit for each ten years
        for(unsigned int year{1}; year <= 10; year += 1)
        {
            double amount = principal * pow(1.0 + rate, year);

            cout << setw(4) << year << setw(20) << amount << endl;
        }

        newRate += 0.01;
    }
}

//Exercise 5.14 Triangle printing program
void printTriangleOne()
{
    int maxCounter{1};

    for(int i{1}; i <= 10; i += 1)
    {


        for(int j{1}; j <= maxCounter; j += 1)
        {
            cout << "*";
        }
        maxCounter += 1;
        cout << endl;
    }

}

void printTriangleTwo()
{
    int maxCounter{10};

    for(int i{1}; i <= 10; i += 1)
    {


        for(int j{1}; j <= maxCounter; j += 1)
        {
            cout << "*";
        }
        maxCounter -= 1;
        cout << endl;
    }
}

void printTriangleThree()
{
    int asterisks{10};
    int spaces{0};

    for(int i{1}; i <= 10; i += 1)
    {
        //Print spaces
        for(int j{0}; j < spaces; j += 1)
        {
            cout << " ";
        }

        //Print asterisks
        for(int k{0}; k < asterisks; k += 1)
        {
            cout << "*";
        }

        spaces += 1;
        asterisks -= 1;
        cout << endl;
    }
}

void printTriangleFour()
{
    int asterisks{1};
    int spaces{9};

    for(int i{1}; i <= 10; i += 1)
    {
        //Print spaces
        for(int j{0}; j < spaces; j += 1)
        {
            cout << " ";
        }

        //Print asterisks
        for(int k{0}; k < asterisks; k += 1)
        {
            cout << "*";
        }

        spaces -= 1;
        asterisks += 1;
        cout << endl;
    }
}

//Main Function
void printTriangles()
{
    printTriangleOne();
    printTriangleTwo();
    printTriangleThree();
    printTriangleFour();
}

int main()
{
    findSmallestValue();

    findOddProducts();

    factorialToTwenty();

    calcInterest();

    printTriangles();

    return 0;
}
