for (int k = 0; k < n * n; ++k) {
		for (int i = 0; i < n; ++i) {
			for (int j = 0; j < n; ++j) {
				if (j != n - 1) {
					if (matrix[i][j + 1] < matrix[i][j]) {
						int tmp = matrix[i][j + 1];
						matrix[i][j + 1] = matrix[i][j];
						matrix[i][j] = tmp;
					}
				}
				else {
					if (i != n - 1) {
						if ((matrix[i + 1][0] < matrix[i][j]) && (i != n - 1)) {
							int tmp = matrix[i + 1][0];
							matrix[i + 1][0] = matrix[i][j];
							matrix[i][j] = tmp;
						}
					}
				}
			}
		}
	}
