public class AufgabeSwitch{

public static void main(String[] args){

java.util.Scanner scanner = new java.util.Scanner(System.in); 
System.out.println("Bitte geben Sie die 1. Zahl ein"); 
int zahl1 = scanner.nextInt(); 


System.out.println("Bitte geben Sie die 2. Zahl ein"); 
int zahl2 = scanner.nextInt();



System.out.println("Bitte geben Sie ein Rechenzeichen ein"); 
Char rechenzeichen = (scanner.next()).charAt(0);

switch(rechenzeichen){


case ('+'): 
System.out.println(zahl1+zahl2);

break;

case ('-'): 
System.out.println(zahl1-zahl2);

break;

case ('*'): 
System.out.println(zahl1*zahl2);

break;

case ('/'):

System.out.println(zahl1/zahl2);

break;

default:

System.out.println("Fehler");
