Float AlienBodyX, AlienBodyY; //alien body
int AlienBodyRadius = 60; // alien body width

int DirectionX = 1;  // X Axis Direction
int DirectionY = 1;  // Y Axis Direction

float SpeedX = 1.5;   
float SpeedY = 1.2;  

float r1; //red1
float g1; //green1
float b1; //blue1
float r2; //red2
float g2; //green2
float b2; //blue2


float AlienHeadX, AlienHeadY; //alien head 1
int AlienHeadRadius = 30; //alien head radius

float AlienHead2X, AlienHead2Y; //alien head 2
float AlienHead3X, AlienHead3Y; //alien head 2
float AlienHead4X, AlienHead4Y; //alien head 2


void draw() {
  background(100);

  AlienBodyX = AlienBodyX + (SpeedX * DirectionX);
  AlienBodyY = AlienBodyY + (SpeedY * DirectionY);

  if (AlienBodyX > width-AlienBodyRadius | AlienBodyX < AlienBodyRadius) {
    DirectionX *= -1;
  }
  if (AlienBodyY > height-AlienBodyRadius | AlienBodyY < AlienBodyRadius) {
    DirectionY *= -1;
  }

   ellipse(AlienBodyX, AlienBodyY, AlienBodyRadius, AlienBodyRadius);
   fill(r1,g1,b1);




  AlienHeadX = AlienHeadX + (SpeedX * DirectionX);
  AlienHeadY = AlienHeadY + (SpeedY * DirectionY);

  if (AlienHeadX > width-AlienHeadRadius | AlienHeadX < AlienHeadRadius) {
    DirectionX *= -1;
  }
  if (AlienHeadY > height-AlienHeadRadius | AlienHeadY < AlienHeadRadius) {
    DirectionY *= -1;
  }

   ellipse(AlienHeadX, AlienHeadY, AlienHeadRadius, AlienHeadRadius);
   fill(r2,g2,b2);

    AlienHead2X = AlienHead2X + (SpeedX * DirectionX);
  AlienHead2Y = AlienHead2Y + (SpeedY * DirectionY);

  if (AlienHead2X > width-AlienHeadRadius | AlienHead2X < AlienHeadRadius) {
    DirectionX *= -1;
  }
  if (AlienHead2Y > height-AlienHeadRadius | AlienHead2Y < AlienHeadRadius) {
    DirectionY *= -1;
  }

   ellipse(AlienHead2X, AlienHead2Y, AlienHeadRadius, AlienHeadRadius);
   fill(r2,g2,b2);

     AlienHead3X = AlienHead3X + (SpeedX * DirectionX);
  AlienHead3Y = AlienHead3Y + (SpeedY * DirectionY);

  if (AlienHead3X > width-AlienHeadRadius | AlienHead3X < AlienHeadRadius) {
    DirectionX *= -1;
  }
  if (AlienHead3Y > height-AlienHeadRadius | AlienHead3Y < AlienHeadRadius) {
    DirectionY *= -1;
  }

   ellipse(AlienHead3X, AlienHead3Y, AlienHeadRadius, AlienHeadRadius);
   fill(r2,g2,b2);

        AlienHead4X = AlienHead4X + (SpeedX * DirectionX);
  AlienHead4Y = AlienHead4Y + (SpeedY * DirectionY);

  if (AlienHead4X > width-AlienHeadRadius | AlienHead4X < AlienHeadRadius) {
    DirectionX *= -1;
  }
  if (AlienHead4Y > height-AlienHeadRadius | AlienHead4Y < AlienHeadRadius) {
    DirectionY *= -1;
  }

   ellipse(AlienHead4X, AlienHead4Y, AlienHeadRadius, AlienHeadRadius);
   fill(r2,g2,b2);
}
