Auth.currentUserInfo()
  .then(info => {
    this.userId = info.id;
    this.username = info.username;
    this.attributes = [];
    if (info['email']) { this.attributes.push({ name: 'email', value: info['email']}); }
    if (info['phone_number']) { this.attributes.push({ name: 'phone_number', value: info['phone_number']}); }
    this.refreshAvatar();
  });
