#define analog0 A18 // Transmission Oil Pressure
#define analog1 A13 // Oil Pressure
#define analog2 A12 // Coolant Pressure
#define analog3 A19 // Fuel Pressure


void setup() {
  Serial.begin(115200);
  Serial.println("Beginning Pressure Test");
  Serial.print("Analog 0");
  Serial.print("\t");
  Serial.print("Analog 1");
  Serial.print("\t");
  Serial.print("Analog 2");
  Serial.print("\t");
  Serial.print("Analog 3");
  Serial.println("");
}

void loop() {
  int reading0 = analogRead(analog0);
  int reading1 = analogRead(analog1);
  int reading2 = analogRead(analog2);
  int reading3 = analogRead(analog3);
  Serial.print(reading0);
  Serial.print("\t");
  Serial.print(reading1);
  Serial.print("\t");
  Serial.print(reading2);
  Serial.print("\t");
  Serial.print(reading3);
  Serial.println("");
}
