startingServer = 0;
allServers = scan(getHostname());

servers = allServers;

if (startingServer == allServers.length) startingServer = 0;

for (server = startingServer; server < servers.length; server++) {
    print('-> target: ' + servers[server]);
    if (getHackingLevel() >= getServerRequiredHackingLevel(servers[server])) {
        if (hasRootAccess(servers[server]) === false && getServerNumPortsRequired(servers[server]) === 0) {
            nuke(servers[server]);
        } else if (hasRootAccess(servers[server]) === false && getServerNumPortsRequired(servers[server]) === 1) {
            brutessh(servers[server]);
            nuke(servers[server]);
        } else if (hasRootAccess(servers[server]) === false && getServerNumPortsRequired(servers[server]) === 2) {
            brutessh(servers[server]);
            ftpcrack(servers[server]);
            nuke(servers[server]);
        } else if (hasRootAccess(servers[server]) === false && getServerNumPortsRequired(servers[server]) === 3) {
            brutessh(servers[server]);
            ftpcrack(servers[server]);
            relaysmtp(servers[server]);
            nuke(servers[server]);
        } else if (hasRootAccess(servers[server]) === false && getServerNumPortsRequired(servers[server]) === 4) {
            /* brutessh(servers[server]);
            ftpcrack(servers[server]);
            relaysmtp(servers[server]);
            httpworm(servers[server]);
            nuke(servers[server]); */
            print('skip lvl 4 hack');
        } else if (hasRootAccess(servers[server]) === false && getServerNumPortsRequired(servers[server]) === 5) {
            /* brutessh(servers[server]);
            ftpcrack(servers[server]);
            relaysmtp(servers[server]);
            httpworm(servers[server]);
            sqlinject(servers[server]);
            nuke(servers[server]); */
            print('skip lvl 5 hack');
        } else {
            print('-> y u no hack target?')
        }

        srvram = getServerRam(servers[server]);
        print('-> target memory: ' + srvram[1] + ' / ' + srvram[0]);

        if (hasRootAccess(servers[server]) === true && srvram[1] >= 5) {
            if (isRunning('loop-hack.script', servers[server]) === false) {
                scp('loop-hack.script', servers[server]);
                exec('loop-hack.script', servers[server], 1);
            }
            if (isRunning('loop-weaken.script', servers[server]) === false) {
                scp('loop-weaken.script', servers[server]);
                exec('loop-weaken.script', servers[server], 1);
            }
            if (isRunning('loop-grow.script', servers[server]) === false) {
                scp('loop-grow.script', servers[server]);
                for (t = 10; t > 0; t--) {
                    exec('loop-grow.script', servers[server], t);
                }
            }
        }
    }

newServers = scan(servers[server]);

for (newServer = 0; newServer < newServers.length; newServer++) {
        isNewServer = true;
        for (checkServer = 0; checkServer < allServers.length; checkServer++) {
            if (newServers[newServer] == 'home' || newServers[newServer] == allServers[checkServer]) {
                isNewServer = false;
            }
            if (isNewServer) {
                allServers = allServers.concat(newServers[newServer]);
            }
        }
    }
}

    startingServer = server;
