CMD:fuelcars(playerid)
{
	if(PlayerInfo[playerid][bAdmin] < 5) return SCM(playerid, COLOR_GRAD1, "     !");
	{
		forveh(i) { VehInfo[i][vFuel] = 100.0; }
		new string[80];
		format(string,80,"* %s   !",PlayerInfo[playerid][pNames]);
		SendClientMessageToAll(COLOR_LIGHTBLUE,string);
	}
	return 1;
}
