//Ex. 4 Lista Reviso
#include <iostream>
#include <conio.h>
#include <math.h>
#include <locale.h>
using namespace std;
int main(){
	setlocale(LC_ALL, "Portuguese");
	int tentativa, valor ;
	float base = 0, altura = 0, area = 0, teste = 0;
	cout << "\n\t\tEx. 4 LISTA REVISO";

	for(valor = 0; valor == 1; tentativa++){
			cout << "\n\n\tInforme a base: ";
			cin >> teste;
			if (teste > 0){
				base = teste;
				valor = 1;
			}

			else{
				cout << tentativa << "" << "tentativa!"; 
				cout << "Nmero invlido!! Digite novamente: ";
			}

	}

	for(valor = 0; valor == 1; tentativa++){
		cout << "\n\n\tInforme a altura: ";
			cin >> teste;
			if (teste > 0){
			altura = teste;
			valor = 1;
			}

			else{
				cout << tentativa << "" << "tentativa!"; 
				cout << "Nmero invlido!! Digite novamente: ";
			}

	}

	cout << "\n\n\tA rea do tringulo retngulo : " << base * altura / 2;
getch();
}
