import java.util.Date;
import java.util.GregorianCalendar;

public class employee {
    public static void main(String[] args) {
    employee_[]staff=new employee_[3];
    staff[0]=new employee_("Rostuk Pisun",750,2018,11,13);
    staff[1]=new employee_("Toxan != Pisun",751,2018,9,23);
    staff[2]=new employee_("Snezhana Prosto",3434,2019,8,12);
    for(employee_ e:staff)
        e.raiseSalary(5);
    for(employee_ e:staff)
        System.out.println("name="+e.getName()+",salary="+e.getSalary()+",hireDay="+e.getHireDay());
    }
}
class employee_{
    private String name;
    private  double salary;
    private Date hireDay;

    public employee_(String n,double s,int year,int month,int day){
    name=n;
    salary=s;
    GregorianCalendar calendar= new GregorianCalendar(year,month-1,day);
    hireDay=calendar.getTime();
    }
    public String getName()
    {
        return  name;
    }
    public double getSalary()
    {
        return  salary;
    }

    public Date getHireDay() {
        return hireDay;
    }
    public void raiseSalary(double byPercent)
    {
        double raise=salary*byPercent/100;
        salary+=raise;
    }
}
