using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace ConsoleApp6
{
    class Program
    {
        static void Main(string[] args)
        {
            var n = Int32.Parse(Console.ReadLine());

            for (int i = 0; i < n; i++)
            {



                if (i!=0 && i!=(n-1))
                {
                    Console.WriteLine();
                    Console.Write("*");

                    for (int b = 0; b <= n+ (n - 3); b++)
                    {
                        Console.Write("/");
                        continue;
                    }

                    Console.Write("*");

                    if (i == n / 2)
                    {
                        for (int g = n * 2; g < (n * 2 + n); g++)
                        {
                            Console.Write("|");
                        }
                    }
                    else for (int g = n * 2; g < (n * 2 + n); g++)
                        {
                            Console.Write(" ");
                        }

                    Console.Write("*");


                    for (int b = 0; b <= n+(n-3) ; b++)
                    {
                        Console.Write("/");
                        continue;
                    }

                    Console.Write("*");


                    continue;
                }
                if(i!=0) Console.WriteLine( );
                for (int j = 0; j < n * 5; j++)
                {
                    if (j == n * 2 )
                    {
                        for (int g = n * 2; g < (n * 2 + n ); g++)
                        {

                            Console.Write(" ");
                        }


                    }
                    if (j == n * 2)
                    {
                        j += n;

                    }

                    Console.Write("*");

                }

            }
            Console.WriteLine();
        }
    }
}
