require "rubygame"
require "rbGooey"
include Rubygame
TTF.setup
class Game
	def initialize
		@main = Main.new
		@main.font_set("freesansbold.ttf", 16)
		@main.screen_set(800, 600)
		@main.bgcolor = [20,70,20]
		@main.fgcolor = [200,200,200]
		@text = TextRender.new(@main)
		@input = TextInput.new(@text)
		@name = Name.new(@main)
		loop
	end
	def loop
		@text.add"Type in \"no\" without the quote to do something and yes to quit"
		@text.set_size(18)
		@text.set_pos(222,330)
		@text.length(5)
		@text.add"."
		@text.add"."
		@text.add"."
		@text.add"Type"
		@text.render_text
		while (1)
			@text.update
			input = @input.input
			if input == "no" 
				@text.clear
				@text.add"Please type your desired name"
				@text.render_text
				name = @input.input
				@text.add(name + " is the name!")
				@text.render_text
				@name.say(name)
			elsif input == "yes"
				exit
			end
		end
	end
end

class Name
	def initialize(main)
		@text = TextRender.new(main)
		@input = TextInput.new(main)
	end
	def say name
		@text.add("Your name?")
		@text.add("Oh, sorry, I forgot. It is #{name} right?")
		@text.add("Type 'quit' to quit.")
		@text.render_text
		while(1)
			input = @input.input
			if input == "quit"
				exit
			end
		end
	end
end
Game.new
