\begin{tikzpicture}[
	scale=.6,
	every node/.style={scale=0.6},
	every node/.style={minimum size=1cm},
	on grid,
	every matrix/.style={ampersand replacement=\&,column sep=2cm,row sep=.6cm}]

    \begin{scope}
        % image width must be scale * 3.6
        \node[inner sep=0] (image) at (0,0) {\includegraphics[width=2.16cm]{sammy.jpg}};

        \fill[white,fill opacity=.5] (image.south west) rectangle (image.north east);
        \draw[black,thick] (image.south west) rectangle (image.north east);
        \draw[step=2mm, black, very thin, shift={(image.south west)}] (0,0) grid (image.north east);

        \draw[red, very thick] (image.north west) rectangle ($(image.north west) + (0.6, -0.6)$);
	\draw[red, very thick, draw opacity=0.5] ($(image.north west) + (0.2, 0)$) rectangle ($(image.north west) + (0.8, -0.6)$);
	\draw[red, very thick, draw opacity=0.25] ($(image.north west) + (0.4, 0)$) rectangle ($(image.north west) + (1.0, -0.6)$);
	\draw[red, very thick, draw opacity=0.125] ($(image.north west) + (0.6, 0)$) rectangle ($(image.north west) + (1.2, -0.6)$);
	\draw[red, very thick, draw opacity=0.125] ($(image.north west) + (0.6, 0)$) rectangle ($(image.north west) + (1.2, -0.6)$);

	\draw[->, blue, thick] ($(image.north west) + (0.3, -0.3)$) -- ($(image.north west) + (0.9, -0.3)$) ;

	\draw [decorate,decoration={brace,mirror,amplitude=10pt},xshift=-4pt,yshift=0pt]
    ($(image.south west) + (0, -0.2)$) -- ($(image.south east) + (0, -0.2)$) node [black,midway,below] {$m$};

    	\draw [decorate,decoration={brace,mirror,amplitude=10pt},xshift=-4pt,yshift=0pt]
    ($(image.north west) + (-0.2, 0)$) -- ($(image.south west) + (-0.2, 0)$) node [black,midway,left] {$n$};
    \end{scope}

    \node[inner sep=0] (conv) at (0.2\textwidth, 0) {*};

    % convolution kernels
     \begin{scope}
    \draw[red!40, very thick] (0.24\textwidth, 1.8) rectangle ($(0.24\textwidth, 1.8) + (0.6, 0.6)$);
    \draw[help lines, step=2mm, red!40, very thin, shift={(0.24\textwidth, 1.8)}] (0, 0) grid (0.6, 0.6);
    \end{scope}

    \begin{scope}
    \draw[green!40, very thick] (0.24\textwidth, 0.8) rectangle ($(0.24\textwidth, 0.8) + (0.6, 0.6)$);
    \draw[help lines, step=2mm, green!40, very thin, shift={(0.24\textwidth, 0.8)}] (0, 0) grid (0.6, 0.6);
    \end{scope}

    \begin{scope}
    \draw[blue!40, very thick] (0.24\textwidth, -0.2) rectangle ($(0.24\textwidth, -0.2) + (0.6, 0.6)$);
    \draw[help lines, step=2mm, blue!40, very thin, shift={(0.24\textwidth, -0.2)}] (0, 0) grid (0.6, 0.6);
    \end{scope}

    \begin{scope}
    \draw[purple!40, very thick] (0.24\textwidth, -1.2) rectangle ($(0.24\textwidth, -1.2) + (0.6, 0.6)$);
    \draw[help lines, step=2mm, purple!40, very thin, shift={(0.24\textwidth, -1.2)}] (0, 0) grid (0.6, 0.6);
    \end{scope}

    \begin{scope}
    \draw[lightgray, very thick] (0.24\textwidth, -2.2) rectangle ($(0.24\textwidth, -2.2) + (0.6, 0.6)$);
    \draw[help lines, step=2mm, lightgray, very thin, shift={(0.24\textwidth, -2.2)}] (0, 0) grid (0.6, 0.6);
    \end{scope}

    \draw [decorate,decoration={brace,mirror,amplitude=10pt},xshift=-4pt,yshift=0pt]
    (0.32\textwidth,-2.3) -- (0.32\textwidth, 2.5) node (brace) [black,midway,right] {$k$};

   \draw[->, black] ($(brace) + (0.5,0)$) -- ($(brace) + (1.25,0)$);

   % output tensor

   \def\weightcount{5}

   \begin{scope}[shift={(0.65\textwidth, -1.0)}]
    \foreach \x in {0,...,\weightcount} {
        \fill[white,fill opacity=1] ($(\x * -0.25, \x * -0.125)$) rectangle ($(3.2, 3.2) - (\x * 0.25, \x * 0.125)$);
        \draw[black,thick] ($(\x * -0.25, \x * -0.125)$) rectangle ($(3.2, 3.2) - (\x * 0.25, \x * 0.125)$);
        \draw[step=2mm, black, very thin, shift={($(\x * -0.25, \x * -0.125)$)}] (0, 0) grid (3.2, 3.2);
    };

    % moar braces
     \draw [decorate,decoration={brace,mirror,amplitude=10pt},xshift=-4pt,yshift=0pt]
    ($(\weightcount * -0.25 + 0.15, \weightcount * - 0.125 - 0.2)$) -- ($(\weightcount * -0.25 + 3.2 + 0.15, \weightcount * -0.125 - 0.2)$) node [black,midway,below] {$m - 2$};

     \draw [decorate,decoration={brace,amplitude=10pt},xshift=4pt,yshift=0pt]
    ($(3.2, 3.2)$) -- ($(3.2, 0)$) node [black,midway,right=0.3] {$n - 2$};

     \draw [decorate,decoration={brace,amplitude=5pt},xshift=-4pt,yshift=4pt]
    ($(\weightcount * -0.25, \weightcount * -0.125 + 3.2)$) -- ($(0, 3.2)$) node [black,midway,above] {$k$};


    \end{scope}


\end{tikzpicture}
