using System;
using System.Collections.Generic;
using System.ComponentModel;
using System.Data;
using System.Drawing;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using System.Windows.Forms;

namespace SPS_Windows_Game_Real
{
    public partial class Form1 : Form
    {
        int compChoice, userChoice;
        int compScore = 0;
        int userScore = 0;

        public Form1()
        {
            InitializeComponent();
        }

        private void rbnScissors_CheckedChanged(object sender, EventArgs e)
        {
            pbxUser.Image = Image.FromFile("Scissors.jpg");

            userChoice = 1;
        }

        private void rbnPaper_CheckedChanged(object sender, EventArgs e)
        {
            pbxUser.Image = Image.FromFile("Paper.jpg");

            userChoice = 2;
        }

        private void rbnStone_CheckedChanged(object sender, EventArgs e)
        {
            pbxUser.Image = Image.FromFile("Stone.jpg");

            userChoice = 3; 
        }

        private void btnQuit_Click(object sender, EventArgs e)
        {
            Application.Exit();
        }

        private void lblUserScore_Click(object sender, EventArgs e)
        {
            lblUserScore.Text = userScore.ToString();
        }

        private void lblComputerScore_Click(object sender, EventArgs e)
        {
            lblComputerScore.Text = compScore.ToString();
        }

        private void label1_Click(object sender, EventArgs e)
        {

        }

        private void btnPlay_Click(object sender, EventArgs e)
        {
            Random r = new Random();                // create a new random object r
            compChoice = r.Next(3) + 1;             // use r to pick a number from 1 to 3

            if (compChoice == 1)
            {
                pbxComputer.Image = Image.FromFile("Scissors.jpg");
            }

            if (compChoice == 2)
            {
                pbxComputer.Image = Image.FromFile("Paper.jpg");
            }

            if (compChoice == 3)
            {
                pbxComputer.Image = Image.FromFile("Stone.jpg");
            }

            checkResult();
        }

        private void Form1_Load(object sender, EventArgs e)
        {
            frmSplash SScreen = new frmSplash();                // create new SScreen from object
            SScreen.Show();                                     // show the splash screen
            System.Threading.Thread.Sleep(2000);                // pause for 2 seconds 
            SScreen.Close();                                    // close the splash
        }

        private void checkResult()
        {
            // IF YOU DRAW

            if (compChoice == userChoice)
            {
                lblResult.Text = "It's a DRAW!";

                compScore = compScore + 1;
                userScore = userScore + 1;

                lblResult.BackColor = Color.White;
                lblResult.ForeColor = Color.Black;
            }

            // IF YOU LOSE

            if (compChoice == 1 && userChoice == 2)
            {
                lblResult.Text = "You LOSE! SCISSORS cuts PAPER";

                compScore = compScore + 2;

                lblResult.BackColor = Color.White;
                lblResult.ForeColor = Color.Black;
            }

            if (compChoice == 3 && userChoice == 1)
            {
                lblResult.Text = "You LOSE! STONE blunts SCISSORS";

                compScore = compScore + 2;

                lblResult.BackColor = Color.White;
                lblResult.ForeColor = Color.Black;
            }

            if (compChoice == 2 && userChoice == 3)
            {
                lblResult.Text = "You LOSE! PAPER covers STONE";

                compScore = compScore + 2;

                lblResult.BackColor = Color.White;
                lblResult.ForeColor = Color.Black;
            }

            // IF YOU WIN

            if (userChoice == 1 && compChoice == 2)
            {
                lblResult.Text = "You WIN! SCISSORS cuts PAPER";

                userScore = userScore + 2;

                lblResult.BackColor = Color.LightGreen;
                lblResult.ForeColor = Color.Blue;
            }

            if (userChoice == 3 && compChoice == 1)
            {
                lblResult.Text = "You WIN! STONE blunts SCISSORS";

                userScore = userScore + 2;

                lblResult.BackColor = Color.LightGreen;
                lblResult.ForeColor = Color.Blue;
            }

            if (userChoice == 2 && compChoice == 3)
            {
                lblResult.Text = "You WIN!  PAPER covers STONE";

                userScore = userScore + 2;

                lblResult.BackColor = Color.LightGreen;
                lblResult.ForeColor = Color.Blue;
            }
        }
    }
}
