package eu.itsflxsh.tabprefix;

import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.player.AsyncPlayerChatEvent;

public class ChatPrefix implements Listener {

	@EventHandler
	public void PrefixChat(AsyncPlayerChatEvent e) {
		Player p= e.getPlayer();
		String Msg = e.getMessage();
		Msg = Msg.replace("&", "&");

		if(p.hasPermission("tabprefix.admin")) {
			e.setFormat("cAdmin 8| " + p.getName() + "c" + Msg);

			if(p.hasPermission("tabprefix.dev")) {
				e.setFormat("bDev 8| " + p.getName() + "b" + Msg);

				if(p.hasPermission("tabprefix.desingner")) {
					e.setFormat("dDesingner 8| " + p.getName() + "d" + Msg);

					if(p.hasPermission("tabprefix.mod")) {
						e.setFormat("2Moderator 8| " + p.getName() + "2" + Msg);

						if(p.hasPermission("tabprefix.sup")) {
							e.setFormat("aSupporter 8| " + p.getName() + "a" + Msg);

							if(p.hasPermission("tabprefix.builder")) {
								e.setFormat("9Builder 8| " + p.getName() + "9" + Msg);

								if(p.hasPermission("tabprefix.youtuber")) {
									e.setFormat("5YouTuber 8| " + p.getName() + "5" + Msg);

									if(p.hasPermission("tabprefix.gold")) {
										e.setFormat("6Gold 8| " + p.getName() + "6" + Msg);

										if(p.hasPermission("tabprefix.owner")) {
											e.setFormat("4Owner 8| " + p.getName() + "4l" + Msg);

											if(p.hasPermission("tabprefix.freund")) {
												e.setFormat("eFreund 8| " + p.getName() + "e" + Msg);

												if(p.hasPermission("tabprefix.spieler")) {
													e.setFormat("7Spieler 8| " + p.getName() + "7" + Msg);

												}
											}
										}
									}
								}
							}
						}
					}
				}
			}
		}
	}
}
