#define _CRT_SECURE_NO_DEPRECATE
#include<stdio.h>

static int iCislo, iCislo2, iVysledok; // Niektore verzie potrebujuglobalne premenne

int main()
{
	iCislo = 27;
	iCislo2 = 3;
	iVysledok = 0;
	__asm {
		MOV EAX, iCislo
		MOV EBX, iCislo2
		ADD EAX, EBX
		MOV iVysledok, EAX
	}

	printf("\nCislo: %d  Vysledok: %d", iCislo, iVysledok);

	printf("\n\nStlac ENTER a skoncime...");
	scanf("?");
	return(0);
}
