var
  frmFase3: TfrmFase3;
  vrSenha: array[1..3] of integer;
  vrSenhaCorreta: array[1..3] of boolean;
  vrSilabas: array[1..18] of String;
  vrRandomPlacing: array[1..18] of integer;
  vrMainCounter: integer;

implementation

uses untBank1, untBank2, untBank4;

{$R *.dfm}

procedure TfrmFase3.FormClose(Sender: TObject; var Action: TCloseAction);
begin
  Application.Terminate;
end;

procedure prcChecarSenha;
begin
  if ((vrSenhaCorreta[1] = true) and
      (vrSenhaCorreta[2] = true) and
      (vrSenhaCorreta[3] = true)) then
  begin
    vrSenhaCorreta[1] := false;
    vrSenhaCorreta[2] := false;
    vrSenhaCorreta[3] := false;
    vrMainCounter := 1;
    vrClockDrop := 0;
    frmFase4.Visible := true;
    frmFase4.Enabled := true;
    frmFase3.Visible := false;
    frmFase3.Enabled := false;
  end
  else
  begin
    if vrMainCounter = 4 then
    begin
      ShowMessage('Voc no digitou a senha correta.');
      vrSenhaCorreta[1] := false;
      vrSenhaCorreta[2] := false;
      vrSenhaCorreta[3] := false;
      vrMainCounter := 1;
      vrClockDrop := 0;
      frmFase1.Visible := true;
      frmFase1.Enabled := true;
      frmFase3.Visible := false;
      frmFase3.Enabled := false;
    end
  end;
end;

procedure prcAtribuirValoresIniciais;
begin
  vrSilabas[1] := 'La';
  vrSilabas[2] := 'Le';
  vrSilabas[3] := 'Li';
  vrSilabas[4] := 'Lo';
  vrSilabas[5] := 'Lu';
  vrSilabas[6] := 'Ha';
  vrSilabas[7] := 'He';
  vrSilabas[8] := 'Hi';
  vrSilabas[9] := 'Ho';
  vrSilabas[10] := 'Hu';
  vrSilabas[11] := 'Fa';
  vrSilabas[12] := 'Fe';
  vrSilabas[13] := 'Fi';
  vrSilabas[14] := 'Fo';
  vrSilabas[15] := 'Fu';
  vrSilabas[16] := 'Ga';
  vrSilabas[17] := 'Ge';
  vrSilabas[18] := 'Gi';

  vrSenha[1] := 13;
  vrSenha[2] := 1;
  vrSenha[3] := 9;

  vrSenhaCorreta[1] := false;
  vrSenhaCorreta[2] := false;
  vrSenhaCorreta[3] := false;

  vrMainCounter := 1;
end;

procedure TfrmFase3.FormCreate(Sender: TObject);
var
  vrContadorPrimario, vrContadorSecundario: integer;
begin
  RANDOMIZE;
//Incio da declarao das variveis fundamentais
  prcAtribuirValoresIniciais;
//Fim da declarao, incio do programa em si
//Incio da randomizao dos valores de posicionamento
  vrRandomPlacing[1] := RANDOM(18) + 1;
  for vrContadorPrimario := 2 to 18 do
  begin
    vrRandomPlacing[vrContadorPrimario] := RANDOM(18) + 1;
    vrContadorSecundario := vrContadorPrimario - 1;
    repeat
    begin
      if vrRandomPlacing[vrContadorPrimario] = vrRandomPlacing[vrContadorSecundario] then
      begin
        vrRandomPlacing[vrContadorPrimario] := RANDOM(18) + 1;
        vrContadorSecundario := vrContadorPrimario - 1;
      end
      else
      begin
        vrContadorSecundario := vrContadorSecundario - 1;
      end;
    end
    until vrContadorSecundario = 0;
  end;
//Fim da randomizao dos valores de posicionamento, incio da atribuio aos botes
  btnSenha1.Caption := vrSilabas[vrRandomPlacing[1]] + ', '
                     + vrSilabas[vrRandomPlacing[2]] + ', '
                     + vrSilabas[vrRandomPlacing[3]];

  btnSenha2.Caption := vrSilabas[vrRandomPlacing[4]] + ', '
                     + vrSilabas[vrRandomPlacing[5]] + ', '
                     + vrSilabas[vrRandomPlacing[6]];

  btnSenha3.Caption := vrSilabas[vrRandomPlacing[7]] + ', '
                     + vrSilabas[vrRandomPlacing[8]] + ', '
                     + vrSilabas[vrRandomPlacing[9]];

  btnSenha4.Caption := vrSilabas[vrRandomPlacing[10]] + ', '
                     + vrSilabas[vrRandomPlacing[11]] + ', '
                     + vrSilabas[vrRandomPlacing[12]];

  btnSenha5.Caption := vrSilabas[vrRandomPlacing[13]] + ', '
                     + vrSilabas[vrRandomPlacing[14]] + ', '
                     + vrSilabas[vrRandomPlacing[15]];

  btnSenha6.Caption := vrSilabas[vrRandomPlacing[16]] + ', '
                     + vrSilabas[vrRandomPlacing[17]] + ', '
                     + vrSilabas[vrRandomPlacing[18]];
end;
//Fim da atribuio, incio dos cliques de boto

procedure TfrmFase3.btnSenha1Click(Sender: TObject);
begin
  if (vrRandomPlacing[1] = vrSenha[vrMainCounter]) or
     (vrRandomPlacing[2] = vrSenha[vrMainCounter]) or
     (vrRandomPlacing[3] = vrSenha[vrMainCounter]) then
  begin
    vrSenhaCorreta[vrMainCounter] := true;
  end;
  vrMainCounter := vrMainCounter + 1;
  prcChecarSenha;
end;

procedure TfrmFase3.btnSenha2Click(Sender: TObject);
begin
  if (vrRandomPlacing[4] = vrSenha[vrMainCounter]) or
     (vrRandomPlacing[5] = vrSenha[vrMainCounter]) or
     (vrRandomPlacing[6] = vrSenha[vrMainCounter]) then
  begin
    vrSenhaCorreta[vrMainCounter] := true;
  end;
  vrMainCounter := vrMainCounter + 1;
  prcChecarSenha;
end;

