begin
  if (vrRandomPlacing[7] = vrSenha[vrMainCounter]) or
     (vrRandomPlacing[8] = vrSenha[vrMainCounter]) or
     (vrRandomPlacing[9] = vrSenha[vrMainCounter]) then
  begin
    vrSenhaCorreta[vrMainCounter] := true;
  end;
  vrMainCounter := vrMainCounter + 1;
  prcChecarSenha;
end;

