begin
  if (vrRandomPlacing[10] = vrSenha[vrMainCounter]) or
     (vrRandomPlacing[11] = vrSenha[vrMainCounter]) or
     (vrRandomPlacing[12] = vrSenha[vrMainCounter]) then
  begin
    vrSenhaCorreta[vrMainCounter] := true;
  end;
  vrMainCounter := vrMainCounter + 1;
  prcChecarSenha;
end;

