procedure TfrmFase3.btnSenha6Click(Sender: TObject);
begin
  if (vrRandomPlacing[16] = vrSenha[vrMainCounter]) or
     (vrRandomPlacing[17] = vrSenha[vrMainCounter]) or
     (vrRandomPlacing[18] = vrSenha[vrMainCounter]) then
  begin
    vrSenhaCorreta[vrMainCounter] := true;
  end;
  vrMainCounter := vrMainCounter + 1;
  prcChecarSenha;
end;

procedure TfrmFase3.Timer1Timer(Sender: TObject);
begin
  vrClockDrop := vrClockDrop + 1;
  if vrClockDrop = 60 then
  begin
    frmFase1.Visible := true;
    frmFase1.Enabled := true;
    frmFase3.Visible := false;
    frmFase3.Enabled := false;
    vrClockDrop := 0;
    prcAtribuirValoresIniciais;
  end;
end;
