#include <stdio.h>

int main(){
	int N,i,a[10000],primo =0,ultimo =0,k=0;
	do{
		scanf("%d",&N);
	}while(N>10000);
	for(i=0;i<N;i++){
		scanf("%d",&a[i]);
	}

		primo = a[0];
		ultimo = a[N-1];
		printf("L'ultimo e' %d\n",ultimo);

        k = N-1;
	   	    for(i=0;i<N;i++){
	   	    	primo = 0;
	   	    	ultimo =0;
	    		primo = a[i];
	    		ultimo = a[k-i];
	    		a[i] = ultimo;
	    		a[k-i] = primo;
	    }
	for(i=0;i<N;i++){
		printf("%d\n",a[i]);
	}
	return 0;

}
