char axis;

void MyKeyboard(unsigned char theKey, int x, int y)
{
	if(theKey == 'd' || 'D')
	{
		cout << "Control the view direction by entering 'x', 'y', or 'z' in lowercase" << endl;
		cin >> axis;
		cout << axis << endl;
	}
	if(theKey == 'e' || theKey == 27)
	{
		exit(0);
	}
	glutPostRedisplay();
}
