import random

def print_res(vars):	
    print(random.choice(vars))

def main():
    s = {'':['',''], '':['','']}
    winer = ['', ' ', '', ' ']
    loser = ['', '   ', ' ']
    word = list(s.keys())[random.randint(0, len(s) - 1)]
    clue = random.choice(s[word])
    print(clue,'...')
    for j in range(5):
        print(' :')
        answer = input()
        if answer == word:
            print_res(winer)
            break
    if answer != word:
        print_res(loser)

main()
