-> No seu comando /MudarNick ponha isso logo abaixo

if (IsPlayerNick(playerid )) // Esse aqui  o bloqueio, caso o Lek use /mudarnick e esteja com o nick bloqueado, essa e a msg que ira aparecer
{
	new StringCat[500];
    	new diaV, mesV, anoV, horaV, minutoV, segundoV;
   	format(Str, sizeof(Str), P_NBloqueado, PlayerName(playerid) );
    	TimestampToDate(BMO_GetInt(Str, "Vencimento"), anoV, mesV, diaV, horaV, minutoV, segundoV, 0);
    	format(Str, sizeof(Str), "{FFFFFF}Crie sua msg %s\n", PlayerName(playerid), playerid);
    	strcat(StringCat, Str);
    	format(Str, sizeof(Str), "{FFFFFF}Crie sua msg\n\n, PlayerName(playerid), playerid);
    	strcat(StringCat, Str);
   	format(Str, sizeof(Str), "{00FFFF}Nick Bloqueado: {FFFFFF}%s[{00FFFF}%d{FFFFFF}]\n\n", PlayerName(playerid), playerid);
    	strcat(StringCat, Str);
    	format(Str, sizeof(Str), "{00FFFF}Data do Desbloqueio: {FFFFFF}%02d/%02d/%02d as %02d:%02d:%02d \n\n", diaV, mesV, anoV, horaV, minutoV, segundoV);
    	strcat(StringCat, Str);
    	ShowPlayerDialog(playerid, D_NICKBLOQUE, DIALOG_STYLE_MSGBOX, "{FF0000}Nick Bloqueado", StringCat, "Fechar", #);
   	return 1;
 }

> Se o player conseguir mudar o nick ponha isso junto para criar o bloqueio na pasta nicks bloqueados
// Exemplo >
SendClientMessage(playerid, COR_VIADO, 	| NICK | O(A) Player Tutorial mudou seu nick para Tutoriais):
SetarN(playerid);
// FIM

-> No fim da gm coloque essas stocks aqui

stock SetarN(playerid) // Voce irar usar o SetarN quando o lek conseguir mudar o Nick para usar essa stock e criar o nick bloqueado na pasta nicks bloqueados
{
	format(Str, sizeof(Str), P_NBloqueado, PlayerName(playerid)); // Pasta Nicks Bloqueados
	if(!IsPlayerNick(playerid)) // Nosso sistema de Nick bloqueado, como se fosse um VIP, ou cargo admin
	{
		BMO_CreateFile(Str); // Para criar o Nick na pasta nicks bloqueados
		BMO_SetInt(Str, "Vencimento", ( gettime() + (150 * 86400) ) ); // Esse e o vencimento, altere apenas os 150 para deixar a quantia de dias que vc queira
	}
	else
	{
		BMO_SetInt(Str, "Vencimento", ( BMO_GetInt(Str, "Vencimento") + (150 * 86400) ) ); // altere apenas os 150 para deixar a quantia de dias que vc queira
	}
	return 1;
}
stock RemoverN(playerid) // Aqui e usado quando o player spawn e o vencimento tenha acabado, ento ser removido da pasta nick bloqueado, essa stock e usada la em cima no OnPlayerSpawn
{
	format(Str, sizeof(Str), P_NBloqueado, PlayerName(playerid) ); // Pasta nicks bloqueados
	BMO_RemoveFile(Str); // Para remover o Nick da pasta
	return 1;
}
stock IsPlayerNick(playerid) // Aqui  nossa varivel para utilizar ela no /Mudarnick como ja esta feito la em cima
{
	format(Str, sizeof(Str), P_NBloqueado, PlayerName(playerid)); // Pasta nicks bloqueados
	if(BMO_FileExists(Str)) // Para verificar se ja esta bloqueado, se no estiver mudara o nick normalmente
		return 1;

	return 0;
}
