if(this.Fill.Size != 0){
	ctx.fillStyle = this.Fill.Color;
	ctx.font = `${this.Fill.Size}px ${this.FontStyle}`;
	ctx.lineWidth = this.Fill.LineWidth;
	// Draw the lines
	if(this._LineMetrics.Fill.Empty){
		this._ComputeMetrics(ctx,this.Fill,this._LineMetrics.Fill,lines);
	}
	ctx.rect(this._LineMetrics.Fill.MinX,this._LineMetrics.Fill.MinY-this.Fill.Size/2,this._LineMetrics.Fill.TotalWidth, this._LineMetrics.Fill.TotalHeight);
	ctx.clip();
	for(let line of this._LineMetrics.Fill.Lines){
		ctx.fillText(line.Text,line.OffsetX,line.OffsetY);
	}
}
