class Vehicle:
    def __init__(self, wheels, lights):
        self.numwheels = wheels
        self.numlights = lights
    def statsMe(self):
        print 'Wheels: ',self.numwheels
        print 'Headlights: ',self.numlights

class Motorcycle < Vehicle:
    def __init__(self):
        super(2,1)

class Car < Vehicle:
    def __init__(self):
        super(4,2)

ducati = Motorcycle.new()
porsche = Car.new()

ducati.statsMe() # outputs wheels: 2, headlights: 1
porsche.statsMe() # outputs wheels: 4, headlights: 1
