// DreamSpeak RPG.cpp : Defines the entry point for the console application.
//

#include "stdafx.h"
#include <iostream>
#include <fstream>
#include <Windows.h>
#include <ctime>
#include <conio.h>
#include <cstdlib>
#include <string>

using namespace std;

short int health = 5, maxhealth = 5;
short int armor = 0, maxarmor = 100;
short int lvl=1, xp=0, xpmax=10;
short int enemyhp = 10, enemyhpmax = 10;
short int enemydmg = 2;
short int openingchoice = 0;
short int playerdmg = 5;
string PlayerNick, Profession;
short int PlayerProfessionChoice;

void ChatColorsPeople()
{
	HANDLE kolor;
	kolor = GetStdHandle(STD_OUTPUT_HANDLE);
	SetConsoleTextAttribute(kolor, FOREGROUND_GREEN | FOREGROUND_INTENSITY);
}

void ChatColorsPlayer()
{
	HANDLE kolor;
	kolor = GetStdHandle(STD_OUTPUT_HANDLE);
	SetConsoleTextAttribute(kolor, FOREGROUND_BLUE | FOREGROUND_GREEN | FOREGROUND_INTENSITY);
}

void ChatColorsNormal()
{
	HANDLE kolor;
	kolor = GetStdHandle(STD_OUTPUT_HANDLE);
	SetConsoleTextAttribute(kolor, 15);
}

void ChatColorsGetLevel()
{
	HANDLE kolor;
	kolor = GetStdHandle(STD_OUTPUT_HANDLE);
	SetConsoleTextAttribute(kolor, 14);
}

void Death()
{
	system("cls");
	system("color 0C");
	cout << "    \nUmarles! Tutaj konczy sie twoja przygoda. Gra zostanie wylaczona za 5 sekund.";
	Sleep(5000);
	exit(0);
}

void HpHud() // Tutaj znajduje sie funkcja odpowiedzialna za wyswietlanie poziomu zdrowia
{
		HANDLE kolor;
		kolor = GetStdHandle(STD_OUTPUT_HANDLE);
		SetConsoleTextAttribute(kolor, 12);
		cout << "Aktualne zdrowie: " << health << "/" << maxhealth;
		SetConsoleTextAttribute(kolor, 15);
}

void ShieldHud()  // Tutaj znajduje sie funkcja odpowiedzialna za wyswietlanie poziomu pancerza
{
		HANDLE kolor;
		kolor = GetStdHandle(STD_OUTPUT_HANDLE);
		SetConsoleTextAttribute(kolor, 14);
		cout << "   Aktualny pancerz: " << armor << "/" << maxarmor;
		SetConsoleTextAttribute(kolor, 15);
}

void LvlHud()
{
	HANDLE kolor;
	kolor = GetStdHandle(STD_OUTPUT_HANDLE);
	SetConsoleTextAttribute(kolor, 13);
	cout << "   Poziom: " << lvl;
	cout << "   XP: " << xp << "/" << xpmax;
	SetConsoleTextAttribute(kolor, 15);
}

void PlayerDmgHud()
{
	HANDLE kolor;
	kolor = GetStdHandle(STD_OUTPUT_HANDLE);
	SetConsoleTextAttribute(kolor, FOREGROUND_RED | FOREGROUND_INTENSITY);
	cout << "   Obrazenia: " << playerdmg;
	SetConsoleTextAttribute(kolor, 15);
}

void Hud()
{
	HpHud();
	ShieldHud();
	LvlHud();
	PlayerDmgHud();
}

void Menu()
{
	short int menuchoice=0, GameStartPercent=0;
	cout << "------------------------\n";
	cout << "  DreamSpeak RPG v0.1" << endl;
	cout << "------------------------\n";
	cout << "\n Zacznij gre [1]";
	cout << "\n Wczytaj gre [2]";
	cout << "\n Wyjdz z gry [3]";
	cout << endl;

	while (menuchoice != 1 && menuchoice != 2 && menuchoice != 3)
	{
		wybor:
		cout << "\nTwoj wybor: ";
		cin >> menuchoice;
		cin.clear();
		cin.ignore(2147483647, '\n');
		cin.sync();
		switch (menuchoice)
		{
		case 1:
			Sleep(100);
			system("cls");
			for (int i = 0; i <= 10; i++)
			{
				cout << "Wczytywanie gry... " << GameStartPercent << "%\n";
				GameStartPercent += 10;
				Sleep(50);
			}
			Sleep(300);
			system("cls");
			break;
		case 2:
			cout << "Wykryto nielegalna wersje systemu Windows. System zapisu zostanie wylaczony.";
			goto wybor;
			break;
		case 3:
			cout << "Gra zostanie wylaczona za 2 sekundy.";
			Sleep(2000);
			exit(0);
			break;
		default:
			system("cls");
			cout << "------------------------\n";
			cout << "  DreamSpeak RPG v0.1" << endl;
			cout << "------------------------\n";
			cout << "\n Zacznij gre [1]";
			cout << "\n Wczytaj gre [2]";
			cout << "\n Wyjdz z gry [3]";
			cout << endl;
			cout << "\nPodales nieprawidlowy numer, wprowadz swoj wybor jeszcze raz.\n";
			break;
		}
	}
}

void PlayerStartInfo()
{
	system("cls");
	cout << " Zaraz rozpoczniesz gre, lecz zalecam na poczatku przeczytac zawarte tutaj informacje.\n\n W grze wystepowac beda rozmowy pomiedzy postaciami. Twoje dialogi beda mialy ";
	ChatColorsPlayer();
	cout << "taki kolor.";
	ChatColorsNormal();
	cout << "\n Dialogi innych osob natomiast beda mialy ";
	ChatColorsPeople();
	cout << "taki kolor.";
	ChatColorsNormal();
	cout << "\n\n Gra w pewnych momentach zatrzymuje sie, aby kontynuowac rozgrywke nalezy wcisnac jakikolwiek klawisz, \n zalecany jest Enter czy Spacja.";
	cout << "\n\n Aby rozpoczac gre wcisnij jakikolwiek przycisk...";
	_getch();
	Sleep(200);
	system("cls");
}

void GameOpening()
{
	ChatColorsPeople();
	cout << "\n\n Nieznany: O, skad tu sie wziales? Nie widzialem cie wczesniej... jak sie nazywasz?";
	ChatColorsPlayer();
	cout << "\n Ty: (Wprowadz nazwe swojej postaci): ";
	getline(cin, PlayerNick);
	Sleep(200);
	ChatColorsPeople();
	cout << " Nieznany: Milo mi cie poznac " << PlayerNick;
	ChatColorsPlayer();
	Sleep(500);
	cout << "\n Ty: Rowniez jestem kontent, ze cie poznalem. Jak cie zwa?";
	ChatColorsPeople();
	Sleep(1000);
	cout << "\n Nieznany: Wspaniale imie! Z ciekawosci, kim jestes z zawodu?";
	ChatColorsNormal();
	cout << "\n (Musisz zadecydowac jaka profesja chcesz grac. [1] - Wojownik   [2] - Lucznik: ";
	ChatColorsPlayer();
	cin >> PlayerProfessionChoice;
	while (PlayerProfessionChoice != 1 && PlayerProfessionChoice != 2)
	{
		ChatColorsNormal();
		cout << "\n Podana profesja nie istnieje!";
		cout << "\n (Zdecyduj jaka profesja chcesz grac. [1] - Wojownik   [2] - Lucznik: ";
		ChatColorsPlayer();
		cin >> PlayerProfessionChoice;
		cin.clear();
		cin.ignore(2147483647, '\n');
		cin.sync();
	}

	if (PlayerProfessionChoice == 1)
	{
		Profession = "Wojownik";
		ChatColorsPeople();
		cout << "\n Nieznany: Och, czyli jestes Wojownikiem, podobnie jak ja!";
	}
	else
	{
		Profession = "Lucznik";
		ChatColorsPeople();
		cout << "\n Nieznany: Och, czyli jestes Lucznikiem. Nigdy za nimi nie przepadalem.";
	}
	ChatColorsPlayer();
	cout << "\n Ty: Dobrze wiedziec, a wracajac do tematu to jak w koncu sie nazywasz?";
	ChatColorsPeople();
	cout << "\n Nieznany: Nie musisz tego wiedziec...";
	Sleep(1500);
	cout << "\n Nieznany: Ciezko oddychasz, czyzbys byl ranny?";
	Sleep(1000);	
	cout << "\n Nieznany: W zasadzie nie interesuje mnie to. Usmieze twoj bol.";
	_getch();
	ChatColorsNormal();

	while (enemyhp)
	{
		system("cls");
		Hud();
		cout << "\n\n -.-.-.-.-.- WALKA -.-.-.-.-.-";

		cout << "\n Statystyki Przeciwnika";
		cout << "\n HP: " << enemyhp << "/" << enemyhpmax;
		cout << "\n DMG: " << enemydmg;
		cout << "\n\n Co chcesz zrobic? \n[1] - Atak\n[2] - Ucieczka\n\nTwoj wybor: ";
		cin >> openingchoice;

		while (openingchoice != 1 && openingchoice != 2)
		{
			system("cls");
			Hud();
			cout << "\n\n -.-.-.-.-.- WALKA -.-.-.-.-.-";

			cout << "\n Statystyki Przeciwnika";
			cout << "\n HP: " << enemyhp << "/" << enemyhpmax;
			cout << "\n DMG: " << enemydmg;
			cout << "\n\n Co chcesz zrobic? \n[1] - Atak\n[2] - Ucieczka\n\nTwoj wybor: ";
			cin >> openingchoice;
			cin.clear();
			cin.ignore(2147483647, '\n');
			cin.sync();
		}

		if (openingchoice == 1)
		{
			enemyhp -= playerdmg;
			cout << "\n Zadajesz 5 pkt obrazen Nieznanemu.";
			Sleep(100);
			health -= enemydmg;
			cout << "\n Przeciwnik zadaje ci 2 pkt obrazen.";
			_getch();

			while(health <= 0)
			{
				cout << "\n Twoje zdrowie spadlo ponizej 0.";
				_getch();
				Death();
			}

		}
		else
		{
			cout << "Nie ma ucieczki!";
			_getch();
		}
		while (enemyhp <= 0)
		{
			system("cls");
			xpmax = 20;
			lvl++;
			health = health + 5;
			maxhealth = 10;
			Hud();
			ChatColorsGetLevel();
			cout << "\n\n Otrzymujesz awans!";
			ChatColorsNormal();
			cout << " Maksymalne zdrowie zwiekszylo sie do 10.";
			cout << "\n\n Nieznany w ostatniej chwili ucieka z pola walki, nie decydujesz sie na poscig.\n Dopiero teraz uswiadamiasz sobie, ze nie wiesz gdzie jestes. ";
			ChatColorsPlayer();
			cout << "\n\n Ty: Co ja tu robie? Ostatnia rzecz jaka pamietam to wyprawa na Gore Przeznaczenia,\n to tam stracilem przytomnosc, musialem w jakis sposob spasc.";
			cout << "\n To by tlumaczylo moje samopoczucie i bol przeszywajacy cale cialo.\n Musze znalezc jakas pomoc..";
			_getch();
			ChatColorsNormal();
			cout << "\n Postanawiasz pojsc w strone lasu w poszukiwaniu zywych istot...";
			Sleep(1500);
			_getch();
			system("cls");
			break;
		}

	}
}

void StageOne()
{
	system("cls");
	Hud();
	cout << "\n\nsiemson";
}

int main()
{
