	plik.open("Saves.txt");
	if (plik)
	{
		cout << "Istnieje";
	}
	else
	{
		cout << " Nie istnieje";
	}v
*/

	srand((unsigned int)time(0));
	ChatColorsNormal();

	Menu(); // wywoanie menu gry
	PlayerStartInfo();
	Hud(); // funkcja odpowiadajaca za wyswietlenie HUDU
	GameOpening(); // rozpoczecie gry; samouczek, wprowadzenie nazwy gracza oraz wybranie profesji.
	StageOne();
	_getch();
}
