public partial class KatsuDb : DbContext
    {
        private static string _connectionString;

        public KatsuDb(string connectionString)
            : base(connectionString)
        {
        }

        public KatsuDb() : base(_connectionString) { }

        protected override void OnModelCreating(DbModelBuilder modelBuilder)
        {
            modelBuilder.HasDefaultSchema("KatsuragiBotSchema");
        }

        public virtual DbSet<User> Users { get; set; }
        public virtual DbSet<Party> Parties { get; set; }
        public virtual DbSet<Channel> Channels { get; set; }
        public virtual DbSet<Guild> Guilds { get; set; }
    }


    public class User
    {
        [Key]
        public int UserIndex { get; set; }
        public string UserId { get; set; }
        [ForeignKey("FParty")]
        public int? PartyIndex { get; set; }

        public Party FParty { get; set; }
    }

    public class Party
    {
        [Key]
        public int PartyIndex { get; set; }
        [ForeignKey("FChannel")]
        public int? ChannelIndex { get; set; }
        [Required]
        public string MessageId { get; set; }
        [Required]
        public string Name { get; set; }
        [Required]
        public byte MaxPlayers { get; set; }
        public DateTime ExpirationDate { get; set; }
        public string Summary { get; set; }

        public virtual ICollection<User> Users { get; set; }
        public virtual Channel FChannel { get; set; }
    }

    public class Guild
    {
        [Key]
        public int GuildIndex { get; set; }
        public string GuildId { get; set; }
        public DateTime? ExpirationDate { get; set; }
        [ForeignKey("FPartyChannel")]
        public int? PartyChannel { get; set; }

        public virtual Channel FPartyChannel { get; set; }
    }

    public class Channel
    {
        [Key]
        public int ChannelIndex { get; set; }
        public string ChannelId { get; set; }
        [ForeignKey("FGuild")]
        public int? GuildIndex { get; set; }

        public virtual Guild FGuild { get; set; }
    }
