text:
/* Read rules before posting */
commentarea .child .usertext textarea, .commentarea .usertext textarea {
    background-image: url('%%commentbg%%');
    background-repeat: no-repeat;
}

.commentarea .usertext textarea:active, .commentarea .usertext textarea:focus {
    background: none repeat scroll 0% 0%;
}


.res-nightmode commentarea .child .usertext textarea, .res-nightmode .commentarea .usertext textarea {
    background-image: url('%%commentbg%%');
    background-repeat: no-repeat;
}

.res-nightmode .commentarea .usertext textarea:active, .res-nightmode .commentarea .usertext textarea:focus {
    background: none repeat scroll 0% 0%;
}

.res-nightmode .commentarea {
    margin-top: 10px;
    margin-right: 304px !important;
}

#header + .side {
    padding-top: 70px;
}

.side .usertext-body.may-blank-within.md-container > .md > blockquote:nth-of-type(1) {
    position: absolute;
    top: 125px;
    margin: 0 0 0 5px;
    padding: 16px 0;
    background-color: #4052ab;
    width: 275px;
    text-align: center;
}
