boolean sprawdz(int i){
	int zm1 = i*i;
	int zm2 = 2*i;

	do {

		do{
			if(zm1%10==zm2%10){
				return true;
			}
			zm2=zm2/10;
		}while(zm2>10)

		zm1=zm1/10;
	}while(zm1>10)

	return false;
}

void wypisz(int n){
	for(int i=1; 1<n; i++){
		if(sprawdz(i)==true){
			cout<<i;
		}
	}
}

int main{
	int n;

	cout<<"Podaj n";
	cin>>n;
	cout<<"wynik programu";
	wypisz(n);
}
