using System;

namespace tESt
{
    class Program
    {
        static void Main(string[] args)
        {
            var incomeLv = double.Parse(Console.ReadLine());      //  480 
            var averageScore = double.Parse(Console.ReadLine());  //  4.60
            var minSalary  = double.Parse(Console.ReadLine());    //  450.00

            double scholarship = 0;


            if (averageScore <= 4.50)
            {
                Console.WriteLine("You cannot get a scholarship!");
            }
            else if (averageScore >= 5.50)
            {

                    scholarship = averageScore * 25;
                    scholarship = Math.Floor(scholarship);
                Console.WriteLine($"You get a scholarship for excellent results {scholarship} BGN");

        }
            else
            {
                if (incomeLv <= minSalary)
                {
                    scholarship = minSalary * 0.35;
                    scholarship = Math.Floor(scholarship);
                    Console.WriteLine($"You get a Social scholarship {scholarship} BGN");
                }
                else
                {
                    Console.WriteLine("You cannot get a scholarship!");
                }
            }
        }
    }
}
