package pab_lap1_gr22_32;

import java.sql.Connection;
import java.sql.DriverManager;
import java.sql.ResultSet;
import java.sql.Statement;
import javafx.application.Application;
import javafx.event.ActionEvent;
import javafx.event.EventHandler;
import javafx.scene.Scene;
import javafx.scene.control.Button;
import javafx.scene.layout.StackPane;
import javafx.stage.Stage;

/**
 *
 * @author administrator
 */
public class PAB_lap1_gr22_32 extends Application {

    @Override
    public void start(Stage primaryStage) {
        Button btn = new Button();
        btn.setText("Pobierz Dane");
        btn.setOnAction(new EventHandler<ActionEvent>() {

            @Override
            public void handle(ActionEvent event) {
                try {
                    // zaadaowanie sterownika
                    Class.forName("com.microsoft.sqlserver.jdbc.SQLServerDriver");
                    //ustanowienie poczenia do bazy
                    //serwer; baza,nazwa uytknowika;haso
                    Connection polaczenie=DriverManager.getConnection( 
                            "jdbc:sqlserver://localhost\\SQL2016Lab;databaseName=PAB","sa","student");
                    //zapytanie SQL
                    Statement skladnia;
                    //przechwowywanie wynikow
                    ResultSet rezultat;
                    skladnia=polaczenie.createStatement();
                    //wykonanie zapytania
                    rezultat=skladnia.executeQuery("SELECT * FROM tabela");
                    //ptla pobieranie wynikow - wiersz po wierszu
                    while(rezultat.next()) {
                        System.out.println(rezultat.getString("id")+", \""+rezultat.getString("opis"));
                    }
                    //zamkniecie polaczenia
                    polaczenie.close();
                }
                catch(Exception e){
                    System.err.println("Bd "+e.getMessage());
                }
                System.out.println("Dane pobrane");
            }
        });

        StackPane root = new StackPane();
        root.getChildren().add(btn);

        Scene scene = new Scene(root, 300, 250);

        primaryStage.setTitle("Aplikacja grupy 22-32");
        primaryStage.setScene(scene);
        primaryStage.show();
    }

    /**
     * @param args the command line arguments
     */
    public static void main(String[] args) {
        launch(args);
    }

}
